import parseFile from "./parseTournFile";
import generateCsv from "./generateCsv";
import React, { useState } from "react";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import Button from '@mui/material/Button';
import { MuiFileInput } from 'mui-file-input';


export default function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isSelected, setIsSelected] = useState(false);
  const [clubData, setClubData] = useState<any>([]);
  const [seperate, setSeperate] = useState<boolean>(false);

  const changeHandler = (newFile: any) => {
    setSelectedFile(newFile);
    setIsSelected(true);
    handleSubmission(newFile);
  };

  async function genCsv() {
    generateCsv(clubData);
  }
  async function handleSubmission(newFile: File) {
    console.log(selectedFile);
    const data = await parseFile(newFile);
    setClubData(data);
  }
  async function handleChange(event: any) {
    console.log(event.target.value);
    setSeperate(event.target.value === "true" ? true : false);
  }
  console.log(!!clubData);

  return (
    <div>
    <div>
  <MuiFileInput placeholder="Select Tournament File" value={selectedFile} onChange={changeHandler} />
</div>
<div>
  <Button  disabled={!!clubData} variant="contained" onClick={genCsv}>Download Spreadsheet</Button>
  </div>
  <div>
  <FormControl>
  <FormLabel id="demo-controlled-radio-buttons-group">Speech and debate judges seperate people?</FormLabel>
  <RadioGroup
    aria-labelledby="demo-controlled-radio-buttons-group"
    name="controlled-radio-buttons-group"
    value={seperate}
    onChange={handleChange}
  >
    <FormControlLabel value={true} control={<Radio />} label="Yes" />
    <FormControlLabel value={false} control={<Radio />} label="No" />
  </RadioGroup>
</FormControl>
</div>
  {clubData && clubData.length > 0 && (
    <table>
      <tbody>
        <tr>
          <th>{"Club"}</th>
          <th>{"Family"}</th>
          <th>{"Fee"}</th>
          <th>{"Judge Requirement"}</th>
        </tr>
        {clubData.map((club: any) => {
          return club.families.map((family: any, index: number) => {
            return (
              <tr key={index}>
                <td>{club.name}</td>
                <td>{family.name}</td>
                <td>{`$${family.fee}`}</td>
                <td style={{textAlign: "right"}}>{family.judgeReq}</td>
              </tr>
            );
          });
        })}
      </tbody>
    </table>
  )}
</div>
  )
}
