import parseFile from "./parseTournFile";
import generateCsv from "./generateCsv";
import React, { useState } from "react";

export default function App() {
  const [selectedFile, setSelectedFile] = useState();
const [isSelected, setIsSelected] = useState(false);
const [clubData, setClubData] = useState<any>([]);

const changeHandler = (event: any) => {
  setSelectedFile(event.target.files[0]);
  setIsSelected(true);
};

async function genCsv() {
  generateCsv(clubData);
}
async function handleSubmission() {
  const data = await parseFile(selectedFile);
  setClubData(data);
}
  return (
    <div>
  <input type="file" name="file" onChange={changeHandler} />

  <button onClick={handleSubmission}>Submit</button>
  <button onClick={genCsv}>Download Spreadsheet</button>

  {clubData && (
    <table>
      <tbody>
        <tr>
          <th>{"Club"}</th>
          <th>{"Family"}</th>
          <th>{"Fee"}</th>
        </tr>
        {clubData.map((club: any) => {
          return club.families.map((family: any, index: number) => {
            return (
              <tr key={index}>
                <td>{club.name}</td>
                <td>{family.name}</td>
                <td>{`$${family.fee}`}</td>
              </tr>
            );
          });
        })}
      </tbody>
    </table>
  )}
</div>
  )
}
